          <code>audio</code>
        </audio>
        <FavoriteSongs
          trackInfo={ {
            trackName,
            discArt,
            previewUrl,
            trackId,
            artistBand,
          } }
        />
      </div>
    );
  }
}

MusicCard.propTypes = {
  previewUrl: PropTypes.string.isRequired,
  trackName: PropTypes.string.isRequired,
  artwork: PropTypes.string,
}.isRequired;

export default MusicCard;
