import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { fetchCurr, addDispenseAction } from '../redux/actions';

class WalletForm extends Component {
  state = {
    value: '',
    description: '',
    currency: 'USD',
    method: 'Dinheiro',
    tag: 'Alimentação',
  };

  componentDidMount() {
    const { dispatch } = this.props;
    dispatch(fetchCurr());
  }

  saveDispenses = async (event) => {
    const { dispatch } = this.props;
    event.preventDefault();
    const currency = await fetch('https://economia.awesomeapi.com.br/json/all');
    const jsonResponse = await currency.json();
    this.setState({ exchangeRates: jsonResponse }, () => {
      dispatch(addDispenseAction(this.state));
      this.setState({
        value: '',
        description: '',
        exchangeRates: '',
      });
    });
  };

  handleChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name]: value,
    });
  };

  render() {
    const { value, description, currency, method, tag } = this.state;
    const { currencies } = this.props;
    return (
      <div className="formularioDespesa">
        <form>
          <labbel>
            Valor da Despesa
            <input
              data-testid="value-input"
              value={ value }
              name="value"
              type="number"
              onChange={ this.handleChange }
            />
          </labbel>
          <labbel>
            Descrição
            <input
              data-testid="description-input"
              value={ description }
              name="description"
              type="text"
              onChange={ this.handleChange }
            />
          </labbel>
          <labbel htmlFor="selectCurrency">
            Moeda
            <select
              id="selectCurrency"
              data-testid="currency-input"
              name="currency"
              value={ currency }
              onChange={ this.handleChange }
            >
              {currencies.map((singleCurrency) => (
                <option
                  key={ singleCurrency }
                  value={ singleCurrency }
                >
                  {singleCurrency}

                </option>
              ))}
            </select>
          </labbel>
          <labbel htmlFor="payment">
            {' '}
            Forma de pagamento
            <select
              id="payment"
              name="method"
              value={ method }
              data-testid="method-input"
              onChange={ this.handleChange }
            >
              <option>Dinheiro</option>
              <option>Cartão de crédito</option>
              <option>Cartão de débito</option>
            </select>
          </labbel>
          <labbel htmlFor="category">
            {' '}
            Gasto feito em:
            <select
              id="category"
              data-testid="tag-input"
              value={ tag }
              name="tag"
              onChange={ this.handleChange }
            >
              <option>Alimentação</option>
              <option>Lazer</option>
              <option>Trabalho</option>
              <option>Transporte</option>
              <option>Saúde</option>
            </select>
          </labbel>
          <button
            type="submit"
            onClick={ this.saveDispenses }
          >
            Adicionar despesa

          </button>
        </form>
      </div>
    );
  }
}

WalletForm.propTypes = {
  dispatch: PropTypes.func.isRequired,
  currencies: PropTypes.instanceOf(Array).isRequired,
};

const mapStateToProps = (state) => ({
  ...state.wallet,
});

export default connect(mapStateToProps)(WalletForm);
