// Esse reducer será responsável por tratar o todas as informações relacionadas as despesas
import { ACTION_CURR, SAVE_EXPENSE_ACTION } from '../actions';

const INITIAL_EXPENSES_STATE = {
  currencies: [], // array de strings das moedas
  expenses: [], // // array de objetos das despesas, com cada objeto ...
  // possuindo as chaves id, value, currency, method, tag, description e exchangeRates
  expenseEdit: false, // valor booleano que indica de uma despesa está sendo editada
  expenseId: 0, // valor do tipo number que armazena o id da despesa sendo editada
};

const wallet = (state = INITIAL_EXPENSES_STATE, action) => {
  switch (action.type) {
  case ACTION_CURR: // em caso de estar realizando requis
    return { ...state, wallet: { ...state.wallet, currencies: action.payload } };
  case SAVE_EXPENSE_ACTION: // da realizacao de salvar a despesa
    return { ...state,
      expenses: [...state.expenses, {
        id: state.expenses.length,
        value: action.payload.value,
        description: action.payload.description,
        currency: action.payload.currency,
        method: action.payload.payment,
        tag: action.payload.category,
        exchangeRates: {},
      }] };
  default:
    return state;
  }
};
export default wallet;
