import React from 'react';
import { connect } from 'react-redux'; // faz conexao de global state
import PropTypes from 'prop-types';
import { currFetch, saveExpensesAction } from '../redux/actions';
import { toHaveAttribute } from '@testing-library/jest-dom/dist/matchers';

class WalletForm extends React.Component {
  state = {
    value: '',
    description: '',
    curr: 'USD',
    payment: 'Dinheiro',
    category: 'Alimentação',
  };

  componentDidMount() { // componentDidMount dispara uma ou mais acoes apos o componente ser renderizado no DOM
    const { dispatch } = this.props;
    dispatch(currFetch()); // dispara a requisicao apos renderizacao de elementos DOM
  }

  // funcao que salva as despesas (seus valores relacionados como valor, descricao...)
  saveExpenses = (event) => {
    const { dispatch } = this.props; // captura o dispatch que iremos utilizar via props
    event.preventDefault();
    dispatch(saveExpensesAction(this.state)); //  // dispara action tendo como parametro o state
    this.setState({ // seta o novo estado
      value: '',
      description: '',
      curr: 'USD',
      payment: 'Dinheiro',
      category: 'Alimentação',
    });
  };

  changinAttributes = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name]: value,
    });
  };

  render() {
    const { value, description, curr, payment, category } = this.state;
    const { currencies } = this.props;

    return (
      <div>
        <form>
          <labbel>
            Valor da Despesa
            <input
              data-testid="value-input"
              value={ valor }
              name="valor"
              type="number"
              onChange={ this.handleChange }
            />
          </labbel>
          <labbel>
            Descrição
            <input
              data-testid="description-input"
              value={ descricao }
              name="descricao"
              type="text"
              onChange={ this.handleChange }
            />
          </labbel>
          <labbel htmlFor="selectCurrency">
            Moeda
            <select
              id="selectCurrency"
              data-testid="currency-input"
              name="moeda"
              value={ moeda }
              onChange={ this.handleChange }
            >
              {currencies.map((currency) => (
                <option
                  key={ currency }
                  value={ currency }
                >
                  {currency}
                </option>
              ))}
            </select>
          </labbel>
          <labbel htmlFor="payment">
            {' '}
            Forma de pagamento
            <select
              id="payment"
              name="pagamento"
              value={ pagamento }
              data-testid="method-input"
              onChange={ this.handleChange }
            >
              <option>Dinheiro</option>
              <option>Cartão de crédito</option>
              <option>Cartão de débito</option>
            </select>
          </labbel>
          <labbel htmlFor="category">
            {' '}
            Gasto feito em:
            <select
              id="category"
              data-testid="tag-input"
              value={ categoria }
              name="categoria"
              onChange={ this.handleChange }
            >
              <option>Alimentação</option>
              <option>Lazer</option>
              <option>Trabalho</option>
              <option>Transporte</option>
              <option>Saúde</option>
            </select>
          </labbel>
        </form>
      </div>
    );
  }
}

export default WalletForm;
