import { ACTION_CURR, ACTION_SAVE_EXPENSE, EXCLUDE_EXP } from '../actions';

const INITIAL_STATE = {
  currencies: [], // array de moedas que vao nas opcoes de moeda
  expenses: [], // array de objetos contendo caracteristicas da despesa, como valor, etc...
  editor: false, // indica se uma despesa esta sendo editada no momento
  idToEdit: 0, // armazena o id da despesa que esta sendo editada
};

const wallet = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case ACTION_CURR:
    return { ...state, currencies: action.payload };
  case ACTION_SAVE_EXPENSE:
    return { ...state,
      expenses: [...state.expenses, {
        id: state.expenses.length,
        ...action.payload,
      }] };
  case DELETE_EXPENSE:
    return { ...state,
      expenses: state.expenses.reduce(((acc, cur) => {
        if (cur.id === action.payload.id) {
          return acc;
        }
        const newExpenses = [...acc, cur];
        return newExpenses;
      }), []),
    };
  default:
    return state;
  }
};

export default wallet;
