export const EMAIL_ACTION = 'SAVE_EMAIL';
export const CURRENCY_ACTION = 'SAVE_CURRENCY_ARRAY';
export const ADD_DISPENSE_ACTION = 'ADD_DISPENSE_ACTION';
export const ADD_CURRENCY_ON_EXPENSE = 'ADD_CURRENCY_ON_EXPENSE';

export const emailAction = (payload) => ({
  type: EMAIL_ACTION,
  payload,
});

export const currencyAction = (payload) => ({
  type: CURRENCY_ACTION,
  payload,
});

export const addDispenseAction = (payload) => ({
  type: ADD_DISPENSE_ACTION,
  payload,
});

export const addCurencyOnExpense = (payload) => ({
  type: ADD_CURRENCY_ON_EXPENSE,
  payload,
});

export const fetchCurrency = () => async (dispatch) => {
  const fistRequisition = await fetch('https://economia.awesomeapi.com.br/json/all');
  const jsonResponse = await fistRequisition.json();
  const currencyArray = Object.keys(jsonResponse);
  const indexUSDT = currencyArray.indexOf('USDT');
  currencyArray.splice(indexUSDT, 1);
  dispatch(currencyAction(currencyArray));
};

export const addCurrencyOnExpense = () => async (dispatch) => {
  const fistRequisition = await fetch('https://economia.awesomeapi.com.br/json/all');
  const jsonResponse = await fistRequisition.json();
  dispatch(addCurencyOnExpense(jsonResponse));
};
