import React from 'react';
import { connect } from 'react-redux'; // faz conexao de global state
import PropTypes from 'prop-types';
import { currFetch, saveExpensesAction } from '../redux/actions';

class WalletForm extends React.Component {
  state = {
    value: '',
    description: '',
    curr: 'USD',
    payment: 'Dinheiro',
    category: 'Alimentação',
  };

  componentDidMount() { // componentDidMount dispara uma ou mais acoes apos o componente ser renderizado no DOM
    const { dispatch } = this.props;
    dispatch(currFetch()); // dispara a requisicao apos renderizacao de elementos DOM
  }

  // funcao que salva as despesas e seus valores relacionados
  saveExpenses = (event) => {
    const { dispatch } = this.props;
    event.preventDefault();
    dispatch(saveExpensesAction(this.state));
    this.setState({
      value: '',
      description: '',
      curr: 'USD',
      payment: 'Dinheiro',
      category: 'Alimentação',
    });
  };


  render() {
    return (
      <div>
        <p data-testid="value-input">despesa</p>
        <p data-testid="description-input">descricao da despesa</p>
        <select data-testid="currency-input">
          <option></option>
        </select>
      </div>
    );
  }
}

export default WalletForm;
