// Coloque aqui suas actions
export const ACTION_EMAIL = 'EMAIL_SAVE';
export const ACTION_CURR = 'ARRAY_CURR_SAVE';
export const ACTION_SAVE_EXPENSE = 'ACTION_SAVE_EXPENSE';
export const ACTION_CURR_ON_EXPENSE = 'ACTION_CURR_ON_EXPENSE';

export const actionEmail = (payload) => ({
  type: ACTION_EMAIL,
  payload,
});

export const actionCurr = (payload) => ({
  type: ACTION_CURR,
  payload,
});

export const actionSaveExpense = (payload) => ({
  type: ACTION_SAVE_EXPENSE,
  payload,
});

export const actionCurrOnExpense = (payload) => ({
  type: ACTION_CURR_ON_EXPENSE,
  payload,
});

export const fetchCurrency = () => async (dispatch) => {
  const fistRequisition = await fetch('https://economia.awesomeapi.com.br/json/all');
  const jsonResponse = await fistRequisition.json();
  const currencyArray = Object.keys(jsonResponse);
  const indexUSDT = currencyArray.indexOf('USDT');
  currencyArray.splice(indexUSDT, 1);
  dispatch(currencyAction(currencyArray));
};

export const currOnExpense = () => async (dispatch) => {
  const fistRequisition = await fetch('https://economia.awesomeapi.com.br/json/all');
  const jsonResponse = await fistRequisition.json();
  dispatch(actionCurrOnExpense(jsonResponse));
};
