import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class Header extends Component {
  // funcao que soma despesas
  sumOfExpenses = () => {
    const { despesas } = this.props;
    const sum = despesas.reduce(((acc, current) => {
      const total = cur.value * cur.exchangeRates[current.currency].ask;
      acc += total;
      return acc;
    }), 0);
    return sum.toFixed(2);
  };

  render() {
    const { email } = this.props;
    return (
      <div>
        <div data-testid="email-field">{ email }</div>
        <div data-testid="total-field">{this.sumOfExpenses()}</div>
        <div data-testid="header-currency-field">BRL</div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  email: state.user.email,
  despesas: state.wallet.expenses,
});

Header.propTypes = {
  email: PropTypes.string.isRequired,
  despesas: PropTypes.instanceOf(Array).isRequired,
};

export default connect(mapStateToProps)(Header);
