export const EMAIL_SAVE_ACTION = 'EMAIL_SAVING';
export const CURRENCY_ACTION = 'SAVE_CURRENCY_ARRAY';
export const ADD_DISPENSE_ACTION = 'ADD_DISPENSE_ACTION';

export const actionEmail = (payload) => ({
  type: EMAIL_SAVE_ACTION,
  payload,
});

export const currencyAction = (payload) => ({
  type: CURRENCY_ACTION,
  payload,
});

export const addDispenseAction = (payload) => ({
  type: ADD_DISPENSE_ACTION,
  payload,
});


export function fetchDogImage() {
  return (dispatch) => {
    dispatch(requestStarted()); // dispara a funcao que mostra que a requisicao esta sendo feita, vai...
    // aparecer que esta "Carrregando"
    fetch("https://economia.awesomeapi.com.br/json/all") // precisamos aqui embaixo passar
    // o retorno do fetch para dentro do nosso state
      .then(response => response.json())
      .then(data => dispatch(data.message)) // o messagem é a URL da imagem do doguinho
  }
}
