// Esse reducer será responsável por tratar o todas as informações relacionadas as despesas

const INITIAL_EXPENSES_STATE = {
  currencies: [], // array de strings das moedas
  expenses: [], // // array de objetos das despesas, com cada objeto ...
  // possuindo as chaves id, value, currency, method, tag, description e exchangeRates
  expenseEdit: false, // valor booleano que indica de uma despesa está sendo editada
  expenseId: 0, // valor do tipo number que armazena o id da despesa sendo editada
};

const reducerWallet = (state = INITIAL_EXPENSES_STATE) => state;

export default reducerWallet;
