export const EMAIL_SAVE_ACTION = 'EMAIL_SAVING';
export const ACTION_CURR = 'SAVING_CURR_ARRAY';
export const ADD_DISPENSE_ACTION = 'ADD_DISPENSE_ACTION';

export const actionEmail = (payload) => ({
  type: EMAIL_SAVE_ACTION,
  payload,
});

export const currAction = (payload) => ({
  type: ACTION_CURR,
  payload,
});

export const saveExpensesAction = (payload) => ({
  type: ADD_DISPENSE_ACTION,
  payload,
});

// Requisicao da API de Moedas
export const currFetch = () => async (dispatch) => {
  const requisitionOne = await fetch('https://economia.awesomeapi.com.br/json/all');
  const responseJSON = await requisitionOne.json();
  const arrayOfCurrencies = Object.keys(responseJSON); // captura as chaves do objeto de moedas
  const gettingUSDT = currencyArray.indexOf('USDT'); // indexof captura o indice do parametro passado
  arrayOfCurrencies.splice(gettingUSDT, 1); // splice 'corta' o valor do array
  dispatch(currAction(arrayOfCurrencies)); // dispara funcao da acao tendo como param o array de moedas
};
