import React from 'react';
import Form from './components/Form';
import Card from './components/Card';

class App extends React.Component {
// state recebe os valores inciais de cada key
  state = {
    cardName: '',
    cardDescription: '',
    cardAttr1: '',
    cardAttr2: '',
    cardAttr3: '',
    cardImage: '',
    cardRare: '',
    cardTrunfo: false,
    hasTrunfo: false,
    isSaveButtonDisabled: true,
  };

  render() {
    // funcao que faz cada key do state receber como valor aquilo que for passado pro target
    const onInputChange = ({ target }) => {
      this.setState({ [target.name]: target.value });
    };
    // funcao que faz botao receber disabled como valor
    const onSaveButtonClick = () => {
      this.setState({ isSaveButtonDisabled: true });
    };

    // valores que serão renderizados na tela, as keys, puxadas atraves de this.state
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      hasTrunfo,
      isSaveButtonDisabled,
    } = this.state;

    return (
      <div>
        <h1>Tryunfo</h1>
        <Form
        // retorno de cada uma das keys do obj state no form, e abaixo no card
        // { ...this.state } => não funcionou, pois não selecionou as keys do obj delcarado acima
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
          isSaveButtonDisabled={ isSaveButtonDisabled }
          onInputChange={ onInputChange }
          onSaveButtonClick={ onSaveButtonClick }
        />
        <Card
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
        />
      </div>
    );
  }
}

export default App;
