import React from 'react';
import Form from './components/Form';
import Card from './components/Card';

class App extends React.Component {
// state recebe os valores inciais de cada key
  state = {
    cardName: '',
    cardDescription: '',
    cardAttr1: '',
    cardAttr2: '',
    cardAttr3: '',
    cardImage: '',
    cardRare: 'normal',
    cardTrunfo: false,
    hasTrunfo: false,
    // eslint-disable-next-line react/no-unused-state
    isSaveButtonDisabled: true,
    cardList: [],
  };

  render() {
    // valores que serão renderizados na tela, as keys, puxadas atraves de this.state
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      hasTrunfo,
      cardList,
    } = this.state;

    const savingFields = () => {
      // valor final booleano de disable button
      let valorFinalDisab = false;
      const maxAttr = 90;
      const maxTotal = 210;
      // se qualquer dos campos estiver vazio, desabilite botão
      if (
        cardName.length === 0
      || cardDescription.length === 0
      || cardImage.length === 0
      || cardRare.length === 0
      ) { valorFinalDisab = true; }
      // se a soma dos atributos for superior ao max permitido, desabilite botao
      if (Number(cardAttr1)
      + Number(cardAttr2)
      + Number(cardAttr3)
      > maxTotal) valorFinalDisab = true;
      // se qualquer dos atributos posuir valor superior ao max permitido, desabilite botao
      if (Number(cardAttr1) > maxAttr
      || Number(cardAttr2) > maxAttr
      || Number(cardAttr3) > maxAttr
      || Number(cardAttr1) < 0
      || Number(cardAttr2) < 0
      || Number(cardAttr3) < 0) { valorFinalDisab = true; }
      // retorne true ou false para desabilitar botao
      return valorFinalDisab;
    };

    // funcao que faz cada key do state receber como valor aquilo que for passado pro target
    const onInputChange = ({ target }) => {
      this.setState({ [target.name]: target.value });
    };
    // funcao que faz botao receber disabled como valor
    const onSaveButtonClick = () => {
      this.setState({
        cardList: [...cardList, { cardName,
          cardDescription,
          cardAttr1,
          cardAttr2,
          cardAttr3,
          cardImage,
          cardRare,
          cardTrunfo,
          isSaveButtonDisabled,
        }],
      });
      this.setState({
        cardName: '',
        cardDescription: '',
        cardImage: '',
        cardAttr1: 0,
        cardAttr2: 0,
        cardAttr3: 0,
        cardRare: 'normal',
      });
    };

    return (
      <div>
        <h1>Tryunfo</h1>
        <Form
        // retorno de cada uma das keys do obj state no form, e abaixo no card
        // { ...this.state } => não funcionou, pois não selecionou as keys do obj delcarado acima
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
          isSaveButtonDisabled={ savingFields() }
          onInputChange={ onInputChange }
          onSaveButtonClick={ onSaveButtonClick }
          saveCheck={ savingFields }
        />
        <Card
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
        />
      </div>
    );
  }
}

export default App;
