import React from 'react';
import Form from './components/Form';
import Card from './components/Card';

class App extends React.Component {
// state recebe os valores inciais de cada key
  state = {
    cardName: '',
    cardDescription: '',
    cardAttr1: '',
    cardAttr2: '',
    cardAttr3: '',
    cardImage: '',
    cardRare: 'normal',
    cardTrunfo: false,
    // hasTrunfo: false,
    isSaveButtonDisabled: true,
  };

  render() {
    // funcao que faz botao receber disabled como valor
    const onSaveButtonClick = () => {
      this.setState({ isSaveButtonDisabled: true });
    };
    // valores que serão renderizados na tela, as keys, puxadas atraves de this.state
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      // hasTrunfo,
      // isSaveButtonDisabled,
    } = this.state;

    const savingFields = () => {
      let rt = false;
      const maxAttNumber = 90;
      const maxAttNumberTotal = 210;
      if (
        cardName.length === 0
      || cardDescription.length === 0
      || cardImage.length === 0
      || cardRare.length === 0
      ) { rt = true; }
      if (Number(cardAttr1)
      + Number(cardAttr2)
      + Number(cardAttr3)
      > maxAttNumberTotal) rt = true;
      if (Number(cardAttr1) > maxAttNumber
      || Number(cardAttr2) > maxAttNumber
      || Number(cardAttr3) > maxAttNumber
      || Number(cardAttr1) < 0
      || Number(cardAttr2) < 0
      || Number(cardAttr3) < 0) { rt = true; }
      return rt;
    };
    // funcao que faz cada key do state receber como valor aquilo que for passado pro target
    const onInputChange = ({ target }) => {
      this.setState({ [target.name]: target.value });
    };

    return (
      <div>
        <h1>Tryunfo</h1>
        <Form
        // retorno de cada uma das keys do obj state no form, e abaixo no card
        // { ...this.state } => não funcionou, pois não selecionou as keys do obj delcarado acima
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          // hasTrunfo={ hasTrunfo }
          isSaveButtonDisabled={ savingFields() }
          onInputChange={ onInputChange }
          onSaveButtonClick={ onSaveButtonClick }
          funcSave={ savingFields }
        />
        <Card
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
        // hasTrunfo={ hasTrunfo }
        />
      </div>
    );
  }
}

export default App;
